var cradle = require("cradle")
var redis  = require("redis")
var log    = require("logly")
log.name("aubongout")
log.mode("debug")


var d = new(cradle.Connection)(process.env.DBHOST | "localhost", 5984,
							{cache: false}).database("aubongout")
var r = new redis.createClient(process.env.RSPORT || 6379)
    r.auth(process.env.RSAUTH || "")

this.d = d
this.r = r
this.l = log
