var c = require("./client.js")

this.create = function create(data, cb){
	if(!data.type) data.type = "page"
	if(data.type != "page") throw("Wrong type of data")
	return c.d.save(data, cb)
}
this.read = function read(what, cb){
	if(!what) what = "list"
	return c.d.view("page/"+what, cb)
}
this.update = function update(id, data, cb){
	return c.d.merge(id, data, cb)
}
this.destroy = function destroy(id, rev, cb){
	return c.d.remove(id, rev, cb)
}
